(10502, 15, 'common_tab', '<table class="table tab-prop table-striped table-hover" width="100%" cellspacing="1">\r\n<tbody>\r\n<tr>\r\n<td class="group" rowspan="1">Thông tin cơ bản</td>\r\n<td class="text"><label class="tips">Trạng thái phát hành</label></td>\r\n<td class="data">Đang phát hành</td>\r\n</tr>\r\n<tr>\r\n<td class="group" rowspan="4">Màn hình &amp; hiển thị</td>\r\n<td class="text"><label class="tips">Kiểu màn hình</label></td>\r\n<td class="data">LED-backlit LCD 16 triệu màu</td>\r\n</tr>\r\n<tr>\r\n<td class="text"><label class="tips">Kích thước màn hình</label></td>\r\n<td class="data">4.0''''</td>\r\n</tr>\r\n<tr>\r\n<td class="text"><label class="tips">Độ phân giải màn hình</label></td>\r\n<td class="data">640 x 1136px</td>\r\n</tr>\r\n<tr>\r\n<td class="text"><label class="tips">Cảm ứng</label></td>\r\n<td class="data">Cảm ứng điện dung, Đa điểm</td>\r\n</tr>\r\n<tr>\r\n<td class="group" rowspan="4">Nhạc chuông</td>\r\n<td class="text"><label class="tips">Báo cuộc gọi, tin nhắn</label></td>\r\n<td class="data">Báo rung, Chuông mp3, Chuông wav</td>\r\n</tr>\r\n<tr>\r\n<td class="text"><label class="tips">Jack cắm audio</label></td>\r\n<td class="data">Không có</td>\r\n</tr>\r\n<tr>\r\n<td class="text"><label class="tips">Có thể tải thêm nhạc chuông</label></td>\r\n<td class="data">Có</td>\r\n</tr>\r\n<tr>\r\n<td class="text"><label class="tips">Có loa ngoài</label></td>\r\n<td class="data">Có</td>\r\n</tr>\r\n<tr>\r\n<td class="group" rowspan="6">Lưu trữ &amp; bộ nhớ</td>\r\n<td class="text"><label class="tips">Danh bạ có thể lưu trữ</label></td>\r\n<td class="data">Không giới hạn</td>\r\n</tr>\r\n<tr>\r\n<td class="text"><label class="tips">Hỗ trợ SMS</label></td>\r\n<td class="data">MMS, SMS (threaded view), IMessage</td>\r\n</tr>\r\n<tr>\r\n<td class="text"><label class="tips">Hỗ trợ Email</label></td>\r\n<td class="data">Email, Push Email</td>\r\n</tr>\r\n<tr>\r\n<td class="text"><label class="tips">Bộ nhớ trong</label></td>\r\n<td class="data">16 GB</td>\r\n</tr>\r\n<tr>\r\n<td class="text"><label class="tips">Dung lượng thẻ nhớ tối đa</label></td>\r\n<td class="data">Không hỗ trợ</td>\r\n</tr>\r\n<tr>\r\n<td class="text"><label class="tips">Loại thẻ nhớ hỗ trợ</label></td>\r\n<td class="data">Không</td>\r\n</tr>\r\n<tr>\r\n<td class="group" rowspan="9">Dữ liệu &amp; Kết nối</td>\r\n<td class="text"><label class="tips">Mạng GPRS</label></td>\r\n<td class="data">Có</td>\r\n</tr>\r\n<tr>\r\n<td class="text"><label class="tips">Mạng EDGE</label></td>\r\n<td class="data">Có</td>\r\n</tr>\r\n<tr>\r\n<td class="text"><label class="tips">Hỗ trợ 3G</label></td>\r\n<td class="data">HSUPA, 5.76 Mbps, HSDPA, 21 Mbps, Rev. A, up to 3.1 Mbps, LTE, DC-HSDPA, 42 Mbps, 100 Mbps DL</td>\r\n</tr>\r\n<tr>\r\n<td class="text"><label class="tips">Trình duyệt web</label></td>\r\n<td class="data">HTML (Safari)</td>\r\n</tr>\r\n<tr>\r\n<td class="text"><label class="tips">Hệ thống định vị GPS</label></td>\r\n<td class="data">A-GPS, GLONASS</td>\r\n</tr>\r\n<tr>\r\n<td class="text"><label class="tips">Bluetooth</label></td>\r\n<td class="data">Có, v4.0 with A2DP</td>\r\n</tr>\r\n<tr>\r\n<td class="text"><label class="tips">Wifi</label></td>\r\n<td class="data">Wi-Fi 802.11 b/g/n, Wi-Fi hotspot, dual-band</td>\r\n</tr>\r\n<tr>\r\n<td class="text"><label class="tips">Hồng ngoại</label></td>\r\n<td class="data">Không</td>\r\n</tr>\r\n<tr>\r\n<td class="text"><label class="tips">Kết nối USB</label></td>\r\n<td class="data">Có, USB 2.0</td>\r\n</tr>\r\n<tr>\r\n<td class="group" rowspan="8">Giải trí &amp; Đa phương tiện</td>\r\n<td class="text"><label class="tips">Máy ảnh chính</label></td>\r\n<td class="data">8.0 MP</td>\r\n</tr>\r\n<tr>\r\n<td class="text"><label class="tips">Máy ảnh phụ</label></td>\r\n<td class="data">Face detection, 720p@30fps, 1.2 MP, FaceTime</td>\r\n</tr>\r\n<tr>\r\n<td class="text"><label class="tips">Quay phim tiêu chuẩn</label></td>\r\n<td class="data">1080p@30fps, 720p@120fps</td>\r\n</tr>\r\n<tr>\r\n<td